import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList onOpen={setIsOpen} />
        {<AddFriendForm isOpen={isOpen} onOpen={setIsOpen} />}
      </div>
    </div>
  );
}

function FriendsList({ isOpen, onOpen }) {
  const friends = initialFriends;

  return (
    <>
      <ul>
        {friends.map((friend) => (
          <Friend friend={friend} key={friend.id} />
        ))}
      </ul>
    </>
  );
}

function AddFriendForm({ onOpen }) {
  const [newFriendName, setNewFriendName] = useState("");
  const [newFriendImageURL, setNewFriendImageURL] = useState("");
  let isFormOpen = false;

  return (
    <div className="form-add-friend">
      {
        <button className="button" onClick={() => onOpen(true)}>
          Add Friend
        </button>
      }
      <input type="text" className="input"></input>
      <input type="text" className="input"></input>
      <button className="button">Add</button>
      <button className="button" onClick={() => onOpen(false)}>
        Close
      </button>
    </div>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name}></img>
      <h3>{friend.name}</h3>

      {friend.balance < 0 && (
        <p className="red">
          You owe {friend.name} ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      <button className="button">Select</button>
    </li>
  );
}
