import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friends, setFriends] = useState(initialFriends);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList friends={friends} />
        {<AddFriendForm friends={friends} onAddFriend={setFriends} />}
      </div>
    </div>
  );
}

function FriendsList({ friends }) {
  return (
    <>
      <ul>
        {friends.map((friend) => (
          <Friend friend={friend} key={friend.id} />
        ))}
      </ul>
    </>
  );
}

function AddFriendForm({ friends, onAddFriend }) {
  const [newFriendName, setNewFriendName] = useState("");
  const [newFriendImageURL, setNewFriendImageURL] = useState("");
  const [isOpen, setIsOpen] = useState(true);

  function handleOnAddNewFriend() {
    const newFriend = {
      id: Math.random(1000),
      name: newFriendName,
      image: newFriendImageURL,
      balance: 10,
    };

    onAddFriend(() => [friends, newFriend]);
  }

  return (
    <form className="form-add-friend" onSubmit={handleOnAddNewFriend}>
      {!isOpen && (
        <button className="button" onClick={() => setIsOpen(true)}>
          Add Friend
        </button>
      )}
      {isOpen && (
        <>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendName(e.target.value)}
          ></input>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendImageURL(e.target.value)}
          ></input>
          <button className="button">Add</button>
          <button className="button" onClick={() => setIsOpen(false)}>
            Close
          </button>
        </>
      )}
    </form>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name}></img>
      <h3>{friend.name}</h3>

      {friend.balance < 0 && (
        <p className="red">
          You owe {friend.name} ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      <button className="button">Select</button>
    </li>
  );
}
