import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friends, setFriends] = useState(initialFriends);
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList friends={friends} />
        <AddFriendForm
          isOpen={isOpen}
          friends={friends}
          onAddFriend={setFriends}
        />
        <button className="button" onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? "Close" : "Add Friend"}
        </button>
      </div>
      <div className="sidebar">
        <BillSplittingForm />
      </div>
    </div>
  );
}

function FriendsList({ friends }) {
  return (
    <>
      <ul>
        {friends.map((friend) => (
          <Friend friend={friend} key={friend.id} />
        ))}
      </ul>
    </>
  );
}

function BillSplittingForm() {
  const [bill, setBill] = useState(0);
  const [yourExpense, setYourExpense] = useState(0);

  function handleSubmit() {}

  return (
    <form className="form-split-bill" onSubmit={handleSubmit}>
      <h2>SPLIT A BILL WITH ANTHONY</h2>
      <Label>💰 Bill value</Label>
      <input
        type="number"
        className="input"
        onChange={(e) => setBill(Number(e.target.value))}
      ></input>

      <Label>🙋‍♂️ Your expense</Label>
      <input
        type="number"
        className="input"
        onChange={(e) => setYourExpense(Number(e.target.value))}
      ></input>

      <Label>👫 Anthony's expense</Label>
      <input type="number" className="input" value={100 - yourExpense}></input>

      <Label>🤑 Who's paying the bill?</Label>
      <select>
        <option>You</option>
        <option>Anthony</option>
      </select>

      <Button>Split bill</Button>
    </form>
  );
}

function AddFriendForm({ isOpen, onAddFriend }) {
  const [newFriendName, setNewFriendName] = useState("");
  const [newFriendImageURL, setNewFriendImageURL] = useState("");

  function handleOnAddNewFriend(e) {
    e.preventDefault();
    const newFriend = {
      id: Math.random(),
      name: newFriendName,
      image: newFriendImageURL,
      balance: 0,
    };

    onAddFriend((items) => [...items, newFriend]);
  }

  return (
    <div>
      {isOpen && (
        <form className="form-add-friend" onSubmit={handleOnAddNewFriend}>
          <Label>👫 Friend's name</Label>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendName(e.target.value)}
          ></input>
          <Label>📷 Friend's Image</Label>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendImageURL(e.target.value)}
          ></input>
          <Button>Add</Button>
        </form>
      )}
    </div>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name}></img>
      <h3>{friend.name}</h3>

      {friend.balance < 0 && (
        <p className="red">
          You owe {friend.name} ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      <Button>Select</Button>
    </li>
  );
}

function Button({ children }) {
  return <button className="button">{children}</button>;
}

function Label({ children }) {
  return <label>{children}</label>;
}
