import axios from "axios";

export const apiClient = axios.create({
  baseURL: "http://localhost:8080/api",
  withCredentials: false,
  headers: {
    "Access-Control-Allow-Origin": "*", // İzin verilen etki alanı
    "Access-Control-Allow-Methods": "PUT, OPTIONS,DELETE,POST", // İzin verilen HTTP metotları
    "Access-Control-Allow-Headers":
      "DNT,X-CustomHeader,Keep-Alive,User-Agent,X-Requested-With,If-Modified-Since,Cache-Control,Content-Type,Content-Range,Range", // İzin verilen başlıklar
  },
});
