import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friends, setFriends] = useState(initialFriends);
  const [isOpen, setIsOpen] = useState(false);
  const [billFriend, setBillFriend] = useState(0);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList friends={friends} onPayBill={setBillFriend} />
        <AddFriendForm
          isOpen={isOpen}
          friends={friends}
          onAddFriend={setFriends}
        />

        <Button onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? "Close" : "Add Friend"}
        </Button>
      </div>
      <div className="sidebar">
        <BillSplittingForm billFriend={billFriend} />
      </div>
    </div>
  );
}

function FriendsList({ friends, onPayBill }) {
  return (
    <>
      <ul>
        {friends.map((friend) => (
          <Friend friend={friend} onPayBill={onPayBill} key={friend.id} />
        ))}
      </ul>
    </>
  );
}

function BillSplittingForm({ billFriend }) {
  const [bill, setBill] = useState(0);
  const [yourExpense, setYourExpense] = useState(0);

  function handleSubmit(e, payPerson) {
    e.preventDefault();
    const balance = yourExpense - (bill - yourExpense);

    if (payPerson === "friend") {
      initialFriends.filter((person) => person.id === billFriend.id).balance =
        Math.round(balance / 2);
    } else {
      initialFriends.filter((person) => person.id === billFriend.id).balance =
        Math.round(-balance / 2);
    }
  }

  return (
    <form
      className="form-split-bill"
      onSubmit={(e) => handleSubmit(e, billFriend)}
    >
      <h2>SPLIT A BILL WITH ANTHONY</h2>
      <Label>üí∞ Bill value</Label>
      <input type="number" className="input"></input>

      <Label>üôã‚Äç‚ôÇÔ∏è Your expense</Label>
      <input
        type="number"
        className="input"
        onChange={(e) => setYourExpense(Number(e.target.value))}
      ></input>

      <Label>üë´ Anthony's expense</Label>
      <input type="number" className="input" value={bill - yourExpense}></input>

      <Label>ü§ë Who's paying the bill?</Label>
      <select>
        <option value="you">You</option>
        <option value="friend">{billFriend.name}</option>
      </select>

      <Button>Split bill</Button>
    </form>
  );
}

function AddFriendForm({ isOpen, onAddFriend }) {
  const [newFriendName, setNewFriendName] = useState("");
  const [newFriendImageURL, setNewFriendImageURL] = useState("");

  function handleOnAddNewFriend(e) {
    e.preventDefault();
    const newFriend = {
      id: Math.random(),
      name: newFriendName,
      image: newFriendImageURL,
      balance: 0,
    };

    onAddFriend((items) => [...items, newFriend]);
  }

  return (
    <div>
      {isOpen && (
        <form className="form-add-friend" onSubmit={handleOnAddNewFriend}>
          <Label>üë´ Friend's name</Label>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendName(e.target.value)}
          ></input>
          <Label>üì∑ Friend's Image</Label>
          <input
            type="text"
            className="input"
            onChange={(e) => setNewFriendImageURL(e.target.value)}
          ></input>
          <Button>Add</Button>
        </form>
      )}
    </div>
  );
}

function Friend({ friend, onPayBill }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name}></img>
      <h3>{friend.name}</h3>

      {friend.balance < 0 && (
        <p className="red">
          You owe {friend.name} ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you ${Math.abs(friend.balance)}
        </p>
      )}

      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      <Button onClick={() => onPayBill(friend)}>Select</Button>
    </li>
  );
}

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function Label({ children }) {
  return <label>{children}</label>;
}
